,p_plug_name=>'Line Wise Quality Report'
,p_region_template_options=>'#DEFAULT#:t-Region--scrollBody'
,p_plug_template=>wwv_flow_imp.id(88696158059223175431)
,p_plug_display_sequence=>70
,p_function_body_language=>'PLSQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE ',
'     vDATASET    CLOB;',
'BEGIN',
'',
'    vDATASET := vDATASET||'' <input id="b_print" type="button" class="t-Button01_526" style="float:right;"  onclick="printdiv(''''div_print'''');" value=" Print " onload="openWindow.print()">'';',
'',
'    vDATASET := vDATASET||''<div id="div_print">'';',
'',
'    FOR SBU_INFO IN (',
'        SELECT SHORT_CODE||''-''||SBU_NAME NAME, SBU_ADDRESS, SBU_PHONE, SBU_WEB_URL',
'          FROM ADMINISTRATION_SBU',
'         WHERE ID = 1    ',
'    ) LOOP',
'',
'    vDATASET := vDATASET||''',
'    ',
'        <div class="head-title">',
'            <h2>''||SBU_INFO.NAME||''</h2>',
'            <p>Address: ''||SBU_INFO.SBU_ADDRESS||''.</p>',
'            <p>Web: ''||SBU_INFO.SBU_WEB_URL||''</p>',
'            <p>Mobile:''||SBU_INFO.SBU_PHONE||''</p>',
'        </div>'';',
'    ',
'    END LOOP;',
'',
'       vDATASET := vDATASET||''',
'        <p>From Date: <span style="color:black">''||:P6_DATE_FROM||''</span></p>',
'',
'        <p>To Date: <span style="color:black">''||:P6_DATE_TO||''</span></p>',
'        '';',
'',
'    vDATASET := vDATASET||''',
'    <table>'';',
'    vDATASET := vDATASET||''',
'      <thead class="table-header">',
'        <th>Line Number</th>',
'        <th>Sewing DHU%</th>',
'        <th>Total Production</th>',
'        <th>Total Alter</th>',
'        <th>Total Check</th>',
'        <th>Uneven Shape</th>',
'        <th>Broken Stitch</th>',
'        <th>Dirty Mark</th>',
'        <th>Oil Stain</th>',
'        <th>Down Stitch</th>',
'        <th>Hiking</th>',
'        <th>Improper Tuck</th>',
'        <th>Label Alter</th>',
'        <th>Needle Mark / Hole</th>',
'        <th>Open Seam</th>',
'        <th>Skip Stitch</th>',
'        <th>Pleat</th>',
'        <th>Sleeve / Shoulder Up Down</th>',
'        <th>Puckering</th>',
'        <th>Raw Edge</th>',
'        <th>Shading</th>',
'        <th>Uncut Thread</th>',
'        <th>Others</th>',
'      </thead>'';',
'',
'    FOR QC_ALTER IN (',
'                    SELECT  A.LINE_NUMBER, ROUND(((SUM(TOTAL_ALTER) / SUM(TOTAL_CHECK)) * 100),2) DHU, -- ROUND(AVG((A.DHU)/TOTAL_LINE),2) DHU,',
'                                SUM(A.TOTAL_OUTPUT) TOTAL_OUTPUT, SUM(A.DOWN_STS) DOWN_STS, SUM(A.UNEVEN_SHAP) UNEVEN_SHAP,',
'                                SUM(A.BROKEN_STS) BROKEN_STS, SUM(A.DIRTY_MARK) DIRTY_MARK, SUM(A.OIL_STAIN) OIL_STAIN, SUM(A.HIKING) HIKING, SUM(TOTAL_ALTER) TOTAL_ALTER,  SUM(TOTAL_CHECK) TOTAL_CHECK,',
'                                SUM(A.IMPORPER_TUCK) IMPORPER_TUCK, SUM(A.NEEDLE_MARK_HOLE) NEEDLE_MARK_HOLE, SUM(A.OPEN_SEAM) OPEN_SEAM,',
'                                SUM(A.SKIP_STS) SKIP_STS, SUM(A.PLEAT) PLEAT, SUM(A.SLV_SHOULDER_UPDOWN) SLV_SHOULDER_UPDOWN, SUM(A.PUCKERING) PUCKERING,',
'                                SUM(A.RAWEDGE) RAWEDGE, SUM(A.SHADING) SHADING, SUM(A.UNCUT_THREAD) UNCUT_THREAD, SUM(A.OTHERS) OTHERS, SUM(A.LABEL_ALTER) LABEL_ALTER',
'                        FROM ',
'                        (',
'                            SELECT LINE_NUMBER, COUNT(LINE_NUMBER) TOTAL_LINE, SUM(NVL(DHU,0))  DHU, SUM(NVL(TOTAL_OUTPUT,0)) TOTAL_OUTPUT, ',
'                                    SUM(NVL(TOTAL_ALTER,0)) -  SUM(NVL(UNCUT_THREAD,0)) TOTAL_ALTER, ',
'                                    ( (SUM(NVL(TOTAL_ALTER,0)) - SUM(NVL(UNCUT_THREAD,0)) + SUM(NVL(TOTAL_OUTPUT,0))) ) TOTAL_CHECK,',
'                                    SUM(NVL(DOWN_STS,0)) DOWN_STS, SUM(NVL(UNEVEN_SHAP,0)) UNEVEN_SHAP,  ',
'                                SUM(NVL(BROKEN_STS,0)) BROKEN_STS, SUM(NVL(DIRTY_MARK,0)) DIRTY_MARK, SUM(NVL(OIL_STAIN,0)) OIL_STAIN , SUM(NVL(HIKING,0)) HIKING, ',
'                                SUM(NVL(IMPORPER_TUCK,0)) IMPORPER_TUCK, SUM(NVL(NEEDLE_MARK_HOLE,0)) NEEDLE_MARK_HOLE,  SUM(NVL(OPEN_SEAM,0)) OPEN_SEAM, SUM(NVL(SKIP_STS,0)) SKIP_STS, ',
'                                SUM(NVL(PLEAT,0)) PLEAT,  SUM(NVL(SLV_SHOULDER_UPDOWN,0)) SLV_SHOULDER_UPDOWN, SUM(NVL(PUCKERING,0)) PUCKERING, SUM(NVL(RAWEDGE,0)) RAWEDGE, ',
'                                SUM(NVL(SHADING,0)) SHADING, SUM(NVL(UNCUT_THREAD,0)) UNCUT_THREAD, SUM(NVL(OTHERS,0)) OTHERS, SUM(NVL(LABEL_ALTER,0)) LABEL_ALTER',
'                            FROM QC_TAB',
'                            HAVING   QC_DATE BETWEEN TO_DATE(:P6_DATE_FROM,''DD-MON-YYYY'') AND TO_DATE(:P6_DATE_TO,''DD-MON-YYYY'')',
'                            --QC_DATE BETWEEN :P6_DATE_FROM AND :P6_DATE_TO -- ''06-17-2023''',
'                            --AND LINE_NUMBER = :P6_LINE_NUMBER',
'                            GROUP BY QC_DATE , LINE_NUMBER) A',
'                        GROUP BY A.LINE_NUMBER',
'                        ORDER BY A.LINE_NUMBER ASC',
'    )  LOOP',
'',
'    vDATASET := vDATASET||''',
'      <tr>',
'        <td>''||QC_ALTER.LINE_NUMBER||''</td>',
'        <td>''||QC_ALTER.DHU||''%''||''</td>',
'        <td>''||QC_ALTER.TOTAL_OUTPUT||''</td>',
