,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT s.formula_item_no, f.item_name, f.uom_name,',
'       SUM(Opening_balance) Opening_balance , SUM(rcv_pcs) rcv_pcs, SUM(srtnrcv_pcs) srtnrcv_pcs, SUM(trnrcv_pcs) trnrcv_pcs,',
