'SELECT formula_item_no, Opening_balance, 0 rcv_pcs, 0 srtnrcv_pcs, 0 trnrcv_pcs,',
'        0 sales_pcs, 0 suprtn_pcs, 0 damaged_pcs, 0 WHtrn_pcs',
