'create or replace FUNCTION FUN_GET_ORDMAS_SRNO ( P_TRCD IN VARCHAR2,',
' P_LOC IN NUMBER, P_DT IN DATE)',
'RETURN NUMBER IS',
'V_SR NUMBER;',
'BEGIN',
'',
'SELECT NVL(MAX(ORD_SERIALNO),0) + 1',
'INTO V_SR',
'FROM ORDER_MASTER',
'WHERE ORD_TRCODE = P_TRCD',
'AND TO_CHAR(ORD_MDATE, ''MMRR'')= TO_CHAR(P_DT, ''MMRR'')',
'AND ORD_RLOC = P_LOC;',
'',
'RETURN V_SR;',
'END;',
