'create or replace FUNCTION f_pkno (p_Tab VARCHAR2, p_Col VARCHAR2, p_WhereClause VARCHAR2 DEFAULT ''1=1'', p_StartWith NUMBER DEFAULT 1) ',
'   RETURN NUMBER ',
'IS ',
'   ret_val   NUMBER; ',
'BEGIN ',
'   EXECUTE IMMEDIATE ''SELECT nvl(max('' || p_col || '') + 1,''||p_startwith||'') FROM '' || p_tab||'' WHERE ''||p_WhereClause ',
'                INTO ret_val; ',
' ',
'   RETURN ret_val; ',
'EXCEPTION ',
'   WHEN OTHERS ',
'   THEN ',
'      return 0; ',
'END;',
